package com.aegis.domain;

import com.fasterxml.jackson.annotation.JsonProperty;
import org.springframework.data.annotation.Id;
import org.springframework.data.elasticsearch.annotations.Document;
import org.springframework.data.elasticsearch.annotations.Field;
import org.springframework.data.elasticsearch.annotations.FieldType;

import java.time.Instant;
import java.util.List;
import java.util.Map;

/**
 * Represents a security alert generated by the correlation engine.
 * Alerts are created when detection rules identify potential security threats.
 */
@Document(indexName = "aegis-alerts")
public class Alert {
    
    @Id
    @JsonProperty("id")
    private String id;
    
    @Field(type = FieldType.Date)
    @JsonProperty("time")
    private Instant time;
    
    @Field(type = FieldType.Keyword)
    @JsonProperty("tenant_id")
    private String tenantId;
    
    @Field(type = FieldType.Byte)
    @JsonProperty("severity")
    private int severity;
    
    @Field(type = FieldType.Text)
    @JsonProperty("title")
    private String title;
    
    @Field(type = FieldType.Text)
    @JsonProperty("description")
    private String description;
    
    @Field(type = FieldType.Keyword)
    @JsonProperty("rule_id")
    private String ruleId;
    
    @Field(type = FieldType.Keyword)
    @JsonProperty("rule_name")
    private String ruleName;
    
    @Field(type = FieldType.Keyword)
    @JsonProperty("event_ids")
    private List<String> eventIds;
    
    @Field(type = FieldType.Keyword)
    @JsonProperty("status")
    private AlertStatus status;
    
    @Field(type = FieldType.Date)
    @JsonProperty("acknowledged_at")
    private Instant acknowledgedAt;
    
    @Field(type = FieldType.Keyword)
    @JsonProperty("acknowledged_by")
    private String acknowledgedBy;
    
    @Field(type = FieldType.Object)
    @JsonProperty("context")
    private Map<String, Object> context;
    
    /**
     * Default constructor
     */
    public Alert() {
    }
    
    /**
     * Constructor with basic fields
     */
    public Alert(String id, Instant time, String tenantId, int severity) {
        this.id = id;
        this.time = time;
        this.tenantId = tenantId;
        this.severity = severity;
    }
    
    // Getters and Setters
    
    public String getId() {
        return id;
    }
    
    public void setId(String id) {
        this.id = id;
    }
    
    public Instant getTime() {
        return time;
    }
    
    public void setTime(Instant time) {
        this.time = time;
    }
    
    public String getTenantId() {
        return tenantId;
    }
    
    public void setTenantId(String tenantId) {
        this.tenantId = tenantId;
    }
    
    public int getSeverity() {
        return severity;
    }
    
    public void setSeverity(int severity) {
        this.severity = severity;
    }
    
    public String getTitle() {
        return title;
    }
    
    public void setTitle(String title) {
        this.title = title;
    }
    
    public String getDescription() {
        return description;
    }
    
    public void setDescription(String description) {
        this.description = description;
    }
    
    public String getRuleId() {
        return ruleId;
    }
    
    public void setRuleId(String ruleId) {
        this.ruleId = ruleId;
    }
    
    public String getRuleName() {
        return ruleName;
    }
    
    public void setRuleName(String ruleName) {
        this.ruleName = ruleName;
    }
    
    public List<String> getEventIds() {
        return eventIds;
    }
    
    public void setEventIds(List<String> eventIds) {
        this.eventIds = eventIds;
    }
    
    public AlertStatus getStatus() {
        return status;
    }
    
    public void setStatus(AlertStatus status) {
        this.status = status;
    }
    
    public Instant getAcknowledgedAt() {
        return acknowledgedAt;
    }
    
    public void setAcknowledgedAt(Instant acknowledgedAt) {
        this.acknowledgedAt = acknowledgedAt;
    }
    
    public String getAcknowledgedBy() {
        return acknowledgedBy;
    }
    
    public void setAcknowledgedBy(String acknowledgedBy) {
        this.acknowledgedBy = acknowledgedBy;
    }
    
    public Map<String, Object> getContext() {
        return context;
    }
    
    public void setContext(Map<String, Object> context) {
        this.context = context;
    }
}
